import { Component } from 'react';
import './css/Signup.css'
export default class Signup extends Component {
  constructor(props) {
    super(props)
    this.state = {
      email: '',
      password: '',
      confirmPassword: '',
      firstname: ''
    };
  }
  handleInputChange = (event) => {
    const { value, name } = event.target;
    this.setState({
      [name]: value
    });
    const submitButton = document.getElementById('button')
    if(!Object.values(this.state).includes('')){
      submitButton.classList = ["activatedButton"]
    }
    else{
      submitButton.classList = ["button"]
    }
  }
  onSubmit = (event) => {
    event.preventDefault();
    alert('Authentication coming soon!');
  }
  render() {
    return (
      <div className="Signup">
      <form onSubmit={this.onSubmit}>
        <input
          type="text"
          name="firstname"
          placeholder="First Name"
          value={this.state.firstName}
          onChange={this.handleInputChange}
          required
        />
        <input
          type="email"
          name="email"
          placeholder="Email"
          value={this.state.email}
          onChange={this.handleInputChange}
          required
        />
        <input
          type="password"
          name="password"
          placeholder="Password"
          value={this.state.password}
          onChange={this.handleInputChange}
          required
        />
        <input
          type="password"
          name="confirmPassword"
          placeholder="Confirm Password"
          value={this.state.confirmPassword}
          onChange={this.handleInputChange}
          required
        />
        <input className="button" id="button" type="submit" value="Sign Up" />
      </form>
      <div className="disclaimer">By clicking the "Sign Up button, you are creating a plugsity account, and you agree to Plugsity's terms of use and privacy policy</div>
      <hr/>
      <div className="business-signup">
      <div>Sign up as a business? </div> 
      <div>Get Started</div>
      </div>
      </div>
    );
  }
}
