import {Component} from 'react';
import PropTypes from 'prop-types';


class Tab extends Component {
    static propTypes = {
        activeTab: this.propTypes.string.isRequired,
        label: this.propTypes.string.isRequired,
        onClick: this.propTypes.func.isRequired
    }

    onClick = () => {
        const {label, onClick} = this.props;
        onClick(label);

    }
    render(){
        const{
            onClick,
            props: {
                activeTab,
                label
            },
        } = this;
        let className = 'tab-list-item'
        if(activeTab === label){
            className+='tab-list-active';
        }
        return(<li className={className} onClick={onClick}>{label}</li>)
    }
}

export default Tab;
